package fr.chatelain.reservation;

import java.util.Base64;
import java.util.Random;
import java.util.UUID;
import java.util.concurrent.ThreadLocalRandom;

import fr.chatelain.reservation.model.dto.PhotosDto;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Order;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.TestMethodOrder;
import org.junit.jupiter.api.MethodOrderer.OrderAnnotation;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.boot.test.context.SpringBootTest.WebEnvironment;
import org.springframework.boot.web.server.LocalServerPort;
import org.springframework.http.*;
import org.springframework.web.client.RestTemplate;

import fr.chatelain.reservation.exceptions.RepositoryExeption;
import fr.chatelain.reservation.model.Photos;
import fr.chatelain.reservation.service.PhotosService;
import nl.jqno.equalsverifier.EqualsVerifier;

@SpringBootTest(webEnvironment = WebEnvironment.RANDOM_PORT)
@TestMethodOrder(OrderAnnotation.class)
class TestPhotos {

	private static final String MY_UUID = UUID.randomUUID().toString();

	@LocalServerPort
	private int RANDOM_SERVER_PORT;

	private String baseUrl;

	private String getUrl;

	private String getUrlbyId;

	private String postUrl;

	private String putUrl;

	private String deleteUrl;

	@BeforeEach
	public void initUrl() {
		this.baseUrl = "http://localhost:" + RANDOM_SERVER_PORT + "/api/photos";
		this.getUrl = baseUrl;
		this.getUrlbyId = baseUrl + "/{id}";
		this.postUrl = baseUrl;
		this.putUrl = baseUrl;
		this.deleteUrl = baseUrl + "/{id}";
	}

	@Autowired
	private PhotosService service;

	private RestTemplate restTemplate = new RestTemplate();

	@Test
	@Order(1)
	public void contructorPhotosSuccess() {
		Photos entity = service.getInstance();

		Assertions.assertNotNull(entity.getId());

		Random random = ThreadLocalRandom.current();
		byte[] r = new byte[256];
		random.nextBytes(r);
		String dataPhotos = Base64.getEncoder().encodeToString(r);

		Photos entityFullField = service.getInstance(dataPhotos, "NomPhotos", "typeMimePhotos");

		Assertions.assertNotNull(entityFullField.getData());
		Assertions.assertNotNull(entityFullField.getNom());
		Assertions.assertNotNull(entityFullField.getTypeMime());

	}

	@Test
	@Order(2)
	public void equalsAndHashCodePhotosSuccess() {
		EqualsVerifier.simple().forClass(Photos.class).verify();
	}

	@Test
	@Order(3)
	public void savePhotosSuccess() {
		PhotosDto entityDto = new PhotosDto();
		entityDto.setId(MY_UUID);
		entityDto.setNom("Test photos");
		entityDto.setTypeMime("image/jpeg");
		entityDto.setData("");

		HttpHeaders headers = new HttpHeaders();
		headers.set("X-COM-PERSIST", "true");

		HttpEntity<PhotosDto> request = new HttpEntity<>(entityDto, headers);

		ResponseEntity<String> result = restTemplate.exchange(postUrl, HttpMethod.POST, request, String.class);

		Assertions.assertEquals(HttpStatus.CREATED.value(), result.getStatusCodeValue());
	}


	@Test
	@Order(4)
	public void getPhotosSuccess() {
		ResponseEntity<String> result = restTemplate.exchange(getUrl, HttpMethod.GET, null, String.class);
		Assertions.assertEquals(HttpStatus.OK.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(5)
	public void getPhotosByIdSuccess() {
		ResponseEntity<String> result = restTemplate.exchange(getUrlbyId, HttpMethod.GET, null, String.class, MY_UUID);
		Assertions.assertEquals(HttpStatus.OK.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(6)
	public void updatePhotosSuccess() {
		PhotosDto entityDto = new PhotosDto();
		entityDto.setId(MY_UUID);
		entityDto.setNom("Nom modifié");

		HttpHeaders headers = new HttpHeaders();
		headers.set("X-COM-PERSIST", "true");

		HttpEntity<PhotosDto> request = new HttpEntity<>(entityDto, headers);

		ResponseEntity<String> result = restTemplate.exchange(putUrl, HttpMethod.PUT, request, String.class);

		Assertions.assertEquals(HttpStatus.OK.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(7)
	public void deletePhotosSuccess() {
		ResponseEntity<String> result = restTemplate.exchange(deleteUrl, HttpMethod.DELETE, null, String.class,
				MY_UUID);
		Assertions.assertEquals(HttpStatus.OK.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(8)
	public void savePhotosFailed() {
		PhotosDto entityDto = new PhotosDto();

		HttpHeaders headers = new HttpHeaders();
		headers.set("X-COM-PERSIST", "true");

		HttpEntity<PhotosDto> request = new HttpEntity<>(entityDto, headers);

		ResponseEntity<String> result = restTemplate.exchange(postUrl, HttpMethod.POST, request, String.class);

		Assertions.assertEquals(HttpStatus.NO_CONTENT.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(9)
	public void getPhotosByIdFailed() {
		ResponseEntity<String> result = restTemplate.exchange(getUrlbyId, HttpMethod.GET, null, String.class,
				UUID.randomUUID().toString());
		Assertions.assertEquals(HttpStatus.NO_CONTENT.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(10)
	public void getPhotosFailed() {
		ResponseEntity<String> result = restTemplate.exchange(getUrl, HttpMethod.GET, null, String.class);
		Assertions.assertEquals(HttpStatus.NO_CONTENT.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(11)
	public void updatePhotosFailed() {
		PhotosDto entityDto = new PhotosDto();

		HttpHeaders headers = new HttpHeaders();
		headers.set("X-COM-PERSIST", "true");

		HttpEntity<PhotosDto> request = new HttpEntity<>(entityDto, headers);

		ResponseEntity<String> result = restTemplate.exchange(putUrl, HttpMethod.PUT, request, String.class);

		Assertions.assertEquals(HttpStatus.NO_CONTENT.value(), result.getStatusCodeValue());
	}

	@Test
	@Order(12)
	public void deletePhotosFailed() {
		ResponseEntity<String> result = restTemplate.exchange(deleteUrl, HttpMethod.DELETE, null, String.class,
				UUID.randomUUID().toString());
		Assertions.assertEquals(HttpStatus.NO_CONTENT.value(), result.getStatusCodeValue());
	}
}
